import React, { useState } from "react";
import {
  ChevronRight,
  ChevronLeft,
  Star,
  Users,
  Heart,
  Zap,
  Award,
  Send,
} from "lucide-react";

const SCRIPT_URL =
  "https://script.google.com/macros/s/AKfycbxLXR-fsnJTdq8QjES3VBoDHnhlydD1qF6muQY_-9s0w1601Jv8Sa8WQNMCujz7ZV8/exec";

export default function AmbassadorSurvey() {
  const initial = {
    age: "",
    gender: "",
    spiritual: [],
    mission: [],
    skills: [],
    fun: [],
    speakers: "",
    hope: "",
  };

  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState(() => {
    try {
      const stored = window.localStorage?.getItem("ambassador_answers");
      return stored ? JSON.parse(stored) : initial;
    } catch (e) {
      return initial;
    }
  });
  const [badge, setBadge] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const steps = [
    "intro",
    "identity",
    "spiritual",
    "mission",
    "skills",
    "fun",
    "speakers",
    "hope",
    "finish",
  ];

  function update(field, value) {
    setAnswers((s) => {
      const next = { ...s, [field]: value };
      try {
        window.localStorage?.setItem(
          "ambassador_answers",
          JSON.stringify(next)
        );
      } catch (e) {
        // Handle localStorage errors gracefully
      }
      return next;
    });
  }

  function toggleArray(field, value) {
    setAnswers((s) => {
      const arr = new Set(s[field] || []);
      if (arr.has(value)) arr.delete(value);
      else arr.add(value);
      const next = { ...s, [field]: Array.from(arr) };
      try {
        window.localStorage?.setItem(
          "ambassador_answers",
          JSON.stringify(next)
        );
      } catch (e) {
        // Handle localStorage errors gracefully
      }
      return next;
    });
  }

  function next() {
    setStep((s) => Math.min(s + 1, steps.length - 1));
  }

  function prev() {
    setStep((s) => Math.max(s - 1, 0));
  }

  function computeBadge(ans) {
    const counts = {
      Worshipper:
        ans.fun.includes("singing") || ans.skills.includes("music") ? 1 : 0,
      Trailblazer:
        ans.mission.length >= 2 || ans.skills.includes("leadership") ? 1 : 0,
      Witness:
        ans.spiritual.includes("share") || ans.skills.includes("speaking")
          ? 1
          : 0,
      Builder:
        ans.skills.includes("media") || ans.mission.includes("cleanup") ? 1 : 0,
    };
    const winner = Object.entries(counts).sort((a, b) => b[1] - a[1])[0][0];
    return winner;
  }

  async function handleSubmit() {
    setIsSubmitting(true);
    const generatedBadge = computeBadge(answers);
    setBadge(generatedBadge);

    const payload = {
      age: answers.age,
      gender: answers.gender,
      spiritual: answers.spiritual,
      mission: answers.mission,
      skills: answers.skills,
      fun: answers.fun,
      speakers: answers.speakers,
      hope: answers.hope,
      badge: generatedBadge,
      _secret: "NOZIPHO",
    };

    try {
      await fetch(SCRIPT_URL, {
        method: "POST",
        mode: "no-cors",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      try {
        window.localStorage?.removeItem("ambassador_answers");
      } catch (e) {
        // Handle localStorage errors gracefully
      }

      setTimeout(() => {
        setStep(steps.length - 1);
        setIsSubmitting(false);
      }, 1500);
    } catch (err) {
      console.error("🚨 Error submitting:", err);
      alert("Something went wrong. Please try again.");
      setIsSubmitting(false);
    }
  }

  const progressPercent = (step / (steps.length - 1)) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-4 flex items-center justify-center">
      <div className="w-full max-w-4xl">
        {/* Progress Bar */}
        {step > 0 && step < steps.length - 1 && (
          <div className="mb-8">
            <div className="flex justify-between text-white/70 text-sm mb-2">
              <span>Progress</span>
              <span>{Math.round(progressPercent)}%</span>
            </div>
            <div className="w-full bg-white/20 rounded-full h-2">
              <div
                className="bg-gradient-to-r from-yellow-400 to-orange-500 h-2 rounded-full transition-all duration-500 ease-out"
                style={{ width: `${progressPercent}%` }}
              />
            </div>
          </div>
        )}

        {/* Main Card */}
        <div className="bg-white/95 backdrop-blur-lg rounded-3xl shadow-2xl overflow-hidden">
          <div className="relative">
            {/* Header Gradient */}
            <div className="h-32 bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 relative overflow-hidden">
              <div className="absolute inset-0 bg-black/20"></div>
              <div className="absolute -top-10 -right-10 w-32 h-32 bg-white/10 rounded-full"></div>
              <div className="absolute -bottom-5 -left-5 w-24 h-24 bg-white/10 rounded-full"></div>
              <div className="relative z-10 p-8 text-white">
                <h1 className="text-3xl font-black tracking-tight">
                  Ambassador Camp 2026
                </h1>
                <p className="text-white/90 mt-2 font-medium">
                  Your Mission Journey ✨ Shape a Spirit-filled experience
                </p>
              </div>
            </div>

            {/* Content */}
            <div className="p-8">
              {step === 0 && (
                <div className="text-center space-y-8 animate-fade-in">
                  <div className="w-24 h-24 mx-auto rounded-full flex items-center justify-center shadow-lg overflow-hidden bg-white">
                    {/* Replace the src with your logo URL */}
                    <img
                      src="https://sidadventist.org/wp-content/uploads/2021/06/Ambassador_logo.png"
                      alt="Ambassador Camp Logo"
                      className="w-20 h-20 object-contain"
                      onError={(e) => {
                        // Fallback to star icon if logo fails to load
                        e.target.style.display = "none";
                        e.target.nextElementSibling.style.display = "flex";
                      }}
                    />
                    <div className="w-20 h-20 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-full items-center justify-center shadow-lg hidden">
                      <Star className="w-10 h-10 text-white" />
                    </div>
                  </div>

                  <div className="space-y-4">
                    <h2 className="text-3xl font-bold text-gray-800">
                      Welcome, Ambassador! 🌍
                    </h2>
                    <p className="text-xl text-gray-600 max-w-2xl mx-auto leading-relaxed">
                      Imagine preparing for a divine mission. Your choices will
                      shape an unforgettable camp experience that glorifies God
                      and transforms lives.
                    </p>
                  </div>

                  <div className="flex flex-col sm:flex-row gap-4 justify-center pt-4">
                    <button
                      className="group px-8 py-4 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-2xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300 flex items-center justify-center space-x-2"
                      onClick={next}
                    >
                      <span>Start Mission</span>
                      <ChevronRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
                    </button>

                    <button
                      className="px-8 py-4 border-2 border-gray-300 text-gray-700 rounded-2xl font-semibold hover:border-indigo-400 hover:text-indigo-600 transition-all duration-300"
                      onClick={() =>
                        alert("Share this link with your youth groups!")
